<?phpinclude('slider.class.php');//Listamos los archivos de la carpeta bannerIP$files = folderList();$selectedItems = array_rand($files, 6);$titulos = array("IdeaPaís estrena ciclo “Inquietos por Chile”", "Diego Schalper viajará a EEUU para dar conocer IdeaPaís", 'Hacinamiento y allegados: desafíos de la política habitacional en Chile', 'La vida es Bella');$bajadas = array("En seis sesiones que se realizarán hasta el martes 11 de diciembre, jóvenes universitarios podrán dialogar con destacados empresarios, líderes políticos y autoridades gubernamentales", 				 "Durante el mes de Octubre, nuestro Director Ejecutivo, Diego Schalper, estará recorriendo diversos lugares de Estados Unidos en un viaje que tendrá como objetivo desarrollar",				 "Las nuevas políticas deben propender a fomentar condiciones cada vez más favorables en calidad y espacio por cada miembro del núcleo familiar, de manera que");$colors = array( '#F48660', '#5BD9A3', '#005CAB', '#2FD3CB','#C42260', '#67ABCF');$sliders = fillingTheBanner($selectedItems, $files, $titulos, $bajadas, $colors);function folderList(){    if ($handle = opendir($_SERVER['DOCUMENT_ROOT'].'/images/slider/ejemplo/')){      $files = array();      while (false !== ($file = readdir($handle))) {          if($file != '.' && $file != '..' )            $files[] = $file;      }      closedir($handle);      return $files;    }}function fillingTheBanner($selectedItems, $files, $titulos, $bajadas, $colors){	$sliders = array();	foreach($selectedItems as $item){		$sliders[] = new slider(array(                                        'titulo' 			=> $titulos[array_rand($titulos,1)],				    	'foto'				=> $files[$item],				    	'optBox' 			=> 1,				    	'optBoxColor'		=> $colors[array_rand($colors,1)],				    	'optBoxTitulo'		=> $titulos[array_rand($titulos,1)],				    	'optBoxBajada'		=> $bajadas[array_rand($bajadas,1)],				    	'link'				=> 1,    					'urlLink'			=> ''					));	}	return $sliders;}?><div id="gallery">	    <div id="slides">		    <?php $i = 0; ?>    <?php foreach($sliders as $slider): ?>			        <div class="slide">            <?php if($slider->optBox): ?>					            <div class="slide_text_element" style="background-color: <?php echo $slider->optBoxColor?>;<?php echo ($i==0) ? 'border-left: solid 5px #000;':'';?>">                <div class="slide_wrapper_text">						                    <div class="slide_text_element_titulo"><?php echo $slider->optBoxTitulo?></div>                    <div class="slide_text_element_bajada"><?php echo $slider->optBoxBajada?></div>                </div>            </div>            <?php endif;?>            <div class="slide_img_container">                <div class="slide_img_wrapper">						                    <div class="slide_img_box">                        <div class="slide_shift left">						                            <img src="images/banner/flecha_negra.png">                        </div>                            <?php echo ($slider->link) ? "<a href=\"$slider->urlLink\" target=\"_blank\">" : ''?>                        <img class="image_center" position="absolute" src="images/ejemplos/<?php echo $slider->foto?>" />                            <?php echo ($slider->link) ? "</a>" : ''?>                        <div class="slide_shift right">                            <img src="images/banner/flecha_negra.png">                        </div>                    </div>		                </div>			            </div>        </div>        <?php $i++;?>    <?php endforeach; ?>	    </div></div>